package Ch02;

public class Ch02_11_VariableEx2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String BookName = "자바 프로그래밍";
		String Name = "홍길동";
		int Year = 2023;
		int Amount = 30000;
		
		
		System.out.println("도서명: " + BookName);
		System.out.println("저자: "+ Name);
		System.out.println("도서명: " + BookName + "\n저자: " + Name + "\n출간:" + Year+"년");
		System.out.println("출간: "+ Year+"년");
		System.out.println("정가: "+ Amount+"원");
	
		
		
	}

}
